    React.useEffect(() => {
        setPriceLoading(false);
    }, []);

    return (
        <>
            {isPriceLoading ? (
                <Skeleton variant="rectangular" width="100%" height={172} />
            ) : (
                <FormControl component="fieldset">
                    <RadioGroup
                        row
                        aria-label="layout"
                        value={price}
                        onChange={(e) => handelFilter('price', e.target.value)}
                        name="row-radio-buttons-group"
                    >
                        <Grid container spacing={0.25}>
                            <Grid item xs={6}>
                                <FormControlLabel
                                    value="0-10"
                                    control={<Radio />}
                                    label="Below $10"
