import React from 'react';
import { useDispatch } from 'react-redux';

// material-ui
import { useTheme } from '@material-ui/styles';
import { Button, Divider, InputBase, FormHelperText, Paper, Stack, Typography } from '@material-ui/core';

// third-party
import { useFormik } from 'formik';
import * as yup from 'yup';

// project imports
import CouponCode from './CouponCode';
import { SET_DISCOUNT, SNACKBAR_OPEN } from 'store/actions';

const validationSchema = yup.object({
    code: yup
        .string('Coupon expired')
        .oneOf(['BERRY50', 'FLAT05', 'SUB150', 'UPTO200'], 'Coupon expired')
        .required('Coupon code is required')
});

// ===========================|| CHECKOUT CART - CART DISCOUNT ||=========================== //

const CartDiscount = () => {
    const theme = useTheme();
    const dispatch = useDispatch();

    const [open, setOpen] = React.useState(false);
    const [coupon, setCoupon] = React.useState('');
    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const formik = useFormik({
        enableReinitialize: true,
        initialValues: {
            code: coupon
        },
        validationSchema,
        onSubmit: (values) => {
            dispatch({ type: SET_DISCOUNT, code: values.code });
            dispatch({
                type: SNACKBAR_OPEN,
                open: true,
                message: 'Coupon Add Success',
                variant: 'alert',
                alertSeverity: 'success'
            });
        }
    });

    return (
        <Stack justifyContent="flex-end">
            <Typography align="right" variant="caption" color="error" sx={{ cursor: 'pointer' }} onClick={handleClickOpen}>
                Have a coupon code?
            </Typography>
            <form onSubmit={formik.handleSubmit}>
                <Paper
                    component="div"
                    sx={{
                        p: '8px 4px',
                        display: 'flex',
                        alignItems: 'center',
                        border: '1px solid',
                        borderColor: theme.palette.mode === 'dark' ? theme.palette.dark.light + 30 : 'grey.400'
                    }}
                >
                    <InputBase
                        sx={{ ml: 1, flex: 1, fontWeight: 500 }}
                        fullWidth
                        placeholder="Discount Coupon"
                        inputProps={{ 'aria-label': 'search google maps' }}
                        id="code"
                        name="code"
                        value={formik.values.code}
                        onChange={formik.handleChange}
                        error={Boolean(formik.errors.code)}
                    />
                    <Divider sx={{ height: 28, m: 0.5 }} orientation="vertical" />
                    <Button type="submit" color="primary" aria-label="directions">
                        Apply
                    </Button>
                </Paper>
                {formik.errors.code && (
                    <FormHelperText error id="standard-code">
                        {formik.errors.code}
                    </FormHelperText>
                )}
            </form>
            <CouponCode open={open} handleClose={handleClose} setCoupon={setCoupon} />
        </Stack>
    );
};

export default CartDiscount;
